function FilmPage() {
    const {data} = props
    return <>
        <h1>{data.title}</h1>
        <p>Directed by - {data.director}</p>
    </>
}

FilmPage.getInitialProps = async({query}) => {
    const {filmId} = query.filmId;
    const res = await fetch('https://swapi.co/api/films/${filmId}/')
    const data = await res.json();

    return {
        data
    }
}
